DROP TRIGGER /*!50030 IF EXISTS */ `before_outbound_insert`;

DELIMITER $$

CREATE DEFINER = `root`@`localhost` TRIGGER `before_outbound_insert` BEFORE INSERT ON `outbound` FOR EACH ROW begin

DECLARE v_route_name varChar(255) DEFAULT "";
DECLARE v_buying_price decimal(15,4)  DEFAULT 0;
DECLARE v_selling_price decimal(15,4)  DEFAULT 0;
DECLARE v_currency_code varchar(255)  DEFAULT "";
DECLARE v_currency_id bigint(20)  DEFAULT 0;
DECLARE v_route_info text default "";
DECLARE v_sender_id varchar(255) DEFAULT "";
DECLARE v_network_name varchar(255) DEFAULT "";
DECLARE v_country_name varChar(255) DEFAULT "";
DECLARE v_network_id bigint(20) unsigned DEFAULT 0;
DECLARE v_billing_resp longtext;
DECLARE v_billing_status longtext;
DECLARE v_billing_desc longtext;
DECLARE smsCost decimal(15,4) ;
DECLARE last_insert_id bigint(20) DEFAULT 0;
DECLARE v_dlr_url text;
DECLARE v_temp_no bigint(20);



SET v_temp_no=validateMobileNo(NEW.client_id,NEW.recipient);

IF v_temp_no=0 THEN

SET NEW.status='invalidNumber';
SET NEW.created_at=NOW();
SET NEW.description =JSON_OBJECT("Billing",'NOK',"Billing Status",'invalidNumber not billed','BillingResponse',0,'smsCost',0,'currency_code',0,'network_id',0);
ELSE

SET NEW.recipient=v_temp_no;




SELECT getRoute(NEW.client_id,NEW.recipient,NEW.sender) INTO v_route_info;

select JSON_UNQUOTE(JSON_EXTRACT(v_route_info,'$.sender_id')) INTO v_sender_id;
select JSON_UNQUOTE(JSON_EXTRACT(v_route_info,'$.route_name'))  INTO v_route_name ;
select JSON_UNQUOTE(JSON_EXTRACT(v_route_info,'$.buying_price')) INTO v_buying_price ;
select JSON_UNQUOTE(JSON_EXTRACT(v_route_info,'$.selling_price'))  INTO v_selling_price ;
select JSON_UNQUOTE(JSON_EXTRACT(v_route_info,'$.currency_code')) INTO v_currency_code ;
select JSON_UNQUOTE(JSON_EXTRACT(v_route_info,'$.currency_id')) INTO v_currency_id ;
select JSON_UNQUOTE(JSON_EXTRACT(v_route_info,'$.network_id')) INTO v_network_id ;
select JSON_UNQUOTE(JSON_EXTRACT(v_route_info,'$.network_name')) INTO v_network_name ;
select JSON_UNQUOTE(JSON_EXTRACT(v_route_info,'$.country_name')) INTO v_country_name ;

SET NEW.buying_price= v_buying_price;
SET NEW.selling_price=v_selling_price;

SET NEW.route_used=v_route_name;



SET smsCost=ceil(LENGTH(NEW.message)/160)*v_selling_price;


IF NEW.message_id is null or length(TRIM(NEW.message_id))=0 THEN
SET NEW.message_id=sha1(UUID_SHORT());
END IF;


 SET NEW.message_id=CONCAT("M",round(rand()*1000),"_",NEW.message_id);

SET v_dlr_url=CONCAT("http://172.16.1.15/servicePortalProd/backend/web/index.php?r=dlr%2Findex&message_id=",urlencode(NEW.message_id),"&status=%d&text=%b");



IF new.type in ('scheduled-sms','quick-sms') THEN

SET v_billing_resp = billClient(NEW.client_id,NEW.user_id,smsCost,v_currency_id,CONCAT("SMS to ",NEW.recipient));
select JSON_UNQUOTE(JSON_EXTRACT(v_billing_resp,'$.status')) INTO v_billing_status;
select JSON_UNQUOTE(JSON_EXTRACT(v_billing_resp,'$.description'))  INTO v_billing_desc ;

ELSE  

SET v_billing_status = "not_billed_at_ob_trigger_level";
SET v_billing_desc =  "not_billed_at_ob_trigger_level";
END IF;

if v_billing_status in ('successful','not_billed_at_ob_trigger_level') THEN 


SELECT LAST_INSERT_ID() into last_insert_id;


IF last_insert_id > 0 THEN 
SET NEW.description =JSON_OBJECT("Billing",'OK',"Billing Status",v_billing_desc,"smsCost",smsCost,"Message Sent","OK","SQL_ID",last_insert_id,'network_id',v_network_id);
SET NEW.status="Accepted";
SET NEW.statusDescription=JSON_OBJECT("acceptedAt",NOW());

ELSE
SET NEW.description =JSON_OBJECT("Billing",'OK',"Billing Status",v_billing_desc,"smsCost",smsCost,"Message Sent","NOK","SQL_ID",last_insert_id,'network_id',v_network_id);

END IF;

ELSE 
SET NEW.status='billingFailed';
SET NEW.created_at=NOW();
SET NEW.statusDescription=JSON_OBJECT("billingFailedAt",NOW());
SET NEW.description =JSON_OBJECT("Billing",'NOK',"Billing Status",v_billing_status,'BillingResponse',v_billing_desc,'smsCost',smsCost,'currency_code',v_currency_code,'network_id',v_network_id);

END IF;

 END IF; 
END $$

DELIMITER ;

