DECLARE v_time_since_last_response_before_optout INTEGER DEFAULT 24;
DECLARE v_time_since_last_invite_before_optout INTEGER DEFAULT 24;



DECLARE SELECT_CURSOR CURSOR FOR SELECT surveyID from surveys.surveys s join bulkMessages.clientSourceAddr csa on (csa.addrID=s.responsesShortCodeID) where incomingSourceAddr=in_shortCode and if(completionLimit>0,completedSurveys<completionLimit,1) and  if(endDate is not null,NOW()<endDate,1) and s.status=1 order by surveyID ASC;



DECLARE CONTINUE HANDLER FOR NOT FOUND SET v_finished = 1;


OPEN SELECT_CURSOR;
this_loop: LOOP


SET v_finished=0;

FETCH SELECT_CURSOR INTO var_surveyID;

IF v_finished =1 THEN

         RETURN 0;
         LEAVE this_loop;
         CLOSE SELECT_CURSOR;

     END IF;

call log_msg(concat('getActiveSurveyByShortCodeAndMsisdn for surveyID::',var_surveyID,'::msisdn::',in_MSISDN));




select if(max(sq.questionID) is not null,if(max(sa.questionID) is not null,if( max(sa.questionID) >=max(sq.questionID),1,0),0),0),  if(max(sa.questionID) is not null,1,0),if(round(TIMESTAMPDIFF(SECOND,now(),max(sa.dateCreated))/86400)<=1,0,1) into v_completedThisSurvey,v_participating,v_if_over_24_hour_since_last_answer from surveys.surveys s join surveyQuestions sq on (s.surveyID=sq.surveyID) left join surveyAnswers sa on (s.surveyID=sa.surveyID) left join surveyInMessages sim on (sa.inboxID=sim.messageID) where if(sim.messageID is not null,msisdn=in_MSISDN,1) and s.surveyID=var_surveyID;

if v_completedThisSurvey = 0 and  v_participating=1 and v_if_over_24_hour_since_last_answer=0 THEN
call log_msg(concat('has not completed and is participating::msisdn::',in_MSISDN,'::return::',var_surveyID));
RETURN var_surveyID;
LEAVE this_loop;
CLOSE SELECT_CURSOR;
END IF;

call log_msg(concat('getActiveSurveyByShortCodeAndMsisdn for surveyID::',var_surveyID,'::msisdn::',in_MSISDN));




select if(max(sq.questionID) is not null,if(max(sa.questionID) is not null,if( max(sa.questionID) >=max(sq.questionID),1,0),0),0),  if(max(sa.questionID) is not null,1,0),if(round(TIMESTAMPDIFF(SECOND,now(),max(sa.dateCreated))/86400)<=1,0,1) into v_completedThisSurvey,v_participating,v_if_over_24_hour_since_last_answer from surveys.surveys s join surveyQuestions sq on (s.surveyID=sq.surveyID) left join surveyAnswers sa on (s.surveyID=sa.surveyID) left join surveyInMessages sim on (sa.inboxID=sim.messageID) where if(sim.messageID is not null,msisdn=in_MSISDN,1) and s.surveyID=var_surveyID;

if v_completedThisSurvey = 0 and  v_participating=1 and v_if_over_24_hour_since_last_answer=0 THEN
call log_msg(concat('has not completed and is participating::msisdn::',in_MSISDN,'::return::',var_surveyID));
RETURN var_surveyID;
LEAVE this_loop;
CLOSE SELECT_CURSOR;
END IF;


select surveyID into invite_surveyID from surveys.surveys s join surveys.surveyAllowedParticipants sap using(surveyID) where  if(inviteExpirationTime is not null,NOW()<adddate(startDate,INTERVAL inviteExpirationTime HOUR),1) and (s.surveyID=var_surveyID and v_completedThisSurvey=0) and MSISDN=in_MSISDN and round(time_to_sec(timediff(now(),sap.dateCreated))/86400)<=1  order by participantID desc limit 1 ;

call log_msg(concat('getActiveSurveyByShortCodeAndMsisdn::',in_MSISDN));

if invite_surveyID>0 THEN
call log_msg(concat('invited to surveyID::',var_surveyID,'::','::msisdn::',in_MSISDN,'::return::',invite_surveyID));
RETURN var_surveyID;
LEAVE this_loop;
CLOSE SELECT_CURSOR;

ELSE

call log_msg(concat('getActiveSurveyByShortCodeAndMsisdn::',in_MSISDN,'::not invited to any survey'));
END IF;



END LOOP this_loop;
CLOSE SELECT_CURSOR;


END $$
