--
-- Definition of function `getRoute`
--

DROP FUNCTION IF EXISTS `getRoute`;

DELIMITER $$

/*!50003 SET @TEMP_SQL_MODE=@@SQL_MODE, SQL_MODE='STRICT_TRANS_TABLES,ERROR_FOR_DIVISION_BY_ZERO,NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION' */ $$
CREATE DEFINER=`root`@`localhost` FUNCTION `getRoute`(clientID int(11), msisdn bigint(30), senderID varchar(255)) RETURNS text CHARSET latin1
BEGIN

DECLARE v_route_id bigint(20) DEFAULT 0;
DECLARE v_route_name varChar(255) DEFAULT "";
DECLARE v_buying_price decimal(15,4)  DEFAULT 0;
DECLARE v_selling_price  decimal(15,4)  DEFAULT 0;
DECLARE v_currency_code varchar(255)  DEFAULT "";
DECLARE v_currency_id bigint(20) DEFAULT 0;
DECLARE v_sender_id varChar(255) DEFAULT "";
DECLARE v_network_name varChar(255) DEFAULT "";
DECLARE v_country_name varChar(255) DEFAULT "";
DECLARE v_network_id bigint(20) unsigned DEFAULT 0;
DECLARE v_route_exists tinyint(1) DEFAULT 0;
DECLARE v_route_mapping_type varchar(255);
DECLARE v_tag varchar(255);



select rm.type,r.id route_id,r.name route_name,rm.buying_price,rm.selling_price,c.id currency_id,c.code currency_code,if(overide_sender_id>0,sovr.name,senderID) sender_id,n.id network_id,n.name network_name,cc.name country_name,rm.tag INTO v_route_mapping_type,v_route_id,v_route_name,v_buying_price,v_selling_price,v_currency_id,v_currency_code,v_sender_id,v_network_id,v_network_name,v_country_name,v_tag  from networks n join network_prefixes np on (n.id=np.network_id) join route_mapping rm on (n.id=rm.network_id) join routes r on (rm.route_id=r.id) join currencies c on (c.id=rm.currency_id) left join client_sender_ids csi on (rm.sender_id=csi.sender_id and csi.client_id=clientID) left join sender_ids s on (rm.sender_id=s.id) left join countries cc on (cc.id=n.country_id) left join sender_ids sovr on (sovr.id=rm.overide_sender_id) where (length(msisdn)=np.number_length and substr(msisdn,1,prefix_length)=np.prefix)  and if (rm.client_id is not null,rm.client_id=clientID,1) and if (rm.sender_id is not null,s.name COLLATE utf8mb4_general_ci=senderID,1) order by rm.type ,r.quality desc,rm.priority desc,rm.overide_sender_id DESC limit 1;

if v_route_id >0 THEN
SET v_route_exists=1;
END IF;


RETURN JSON_OBJECT('route_mapping_type',v_route_mapping_type,'route_exist',v_route_exists,'route_name',v_route_name,'buying_price',v_buying_price,'selling_price',v_selling_price,'currency_code',v_currency_code,'currency_id',v_currency_id,'sender_id',v_sender_id,'network_id',v_network_id,'network_name',v_network_name,'country_name',v_country_name,'tag',v_tag);

END $$
