CREATE DEFINER=`root`@`localhost` FUNCTION `validateMobileNo`(in_client_id  bigint(20), in_msisdn bigint(20)) RETURNS bigint(20)
BEGIN

DECLARE v_client_default_dialing_code   int(11) ;
DECLARE v_network_id  bigint(20) ;
DECLARE v_network_id_2  bigint(20) ;
DECLARE temp_num bigint(20);


SELECT n.id into v_network_id from networks n join network_prefixes np on (n.id=np.network_id) join countries c on (c.id=n.country_id) where SUBSTR(in_msisdn,1,prefix_length)=prefix and length(in_msisdn)=number_length limit 1;

IF v_network_id > 0 THEN


RETURN in_msisdn;

END IF;


select  dialing_code INTO  v_client_default_dialing_code from clients c join countries ctr on (c.country_id=ctr.id) where c.id=in_client_id;

SET temp_num=CONCAT(v_client_default_dialing_code,TRIM(LEADING '0' FROM in_msisdn));

SELECT n.id into v_network_id_2 from networks n join network_prefixes np on (n.id=np.network_id) join countries c on (c.id=n.country_id) where SUBSTR(temp_num,1,prefix_length)=prefix and length(temp_num)=number_length limit 1;

IF v_network_id_2 > 0 THEN


RETURN temp_num;

END IF;


RETURN 0;
END $$
